  oDBServer:Info(DBI_GETHEADERSIZE)
  // Same as oDBServer:LUpdate
  oDBServer:Info(DBI_LASTUPDATE)
  // Same as oDBServer:RecSize
  oDBServer:Info(DBI_GETRECSIZE)
  // Get the full path
  oDBServer:Info(DBI_FULLPATH)

