  // 使用宏运算符定义的设置-获取块
  cbSetGet := &("{|SetVal| If(SetVal == NIL, FName, FName := SetVal)}")
  // 使用 FieldBlockSym() 定义的设置-获取块
  String2Symbol("FName")
  cbSetGet := FieldBlockSym(#FNAME)

