NorthWind1.example:

  // Specify that we use the SQLSERVER provider
  SqlDbSetProvider("SQLSERVER")
  // Open a connection to the local Northwind database
  var nHandle := SqlDbOpenConnection("Integrated Security=true;Initial Catalog=Northwind;Server=(local)")
  // Open the customers table in that database
  oServer := DbServer{"Select * From Customers where Country = 'Germany' order by CustomerID"}
  oServer:GoTop()
  ? oServer:FieldGet("CustomerID") // ALFKI
  oServer:Skip(1)
  ? oServer:FieldGet("CustomerID") // BLAUS
  oServer:Close()
  SqlDbCloseConnection(nHandle)

///
